const Product = ({post}) => {
  return (
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}></img>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <button>
        {
          true ? <p>Remove item</p> : <p>Add to cart</p>
        }
      </button>
    </div>
  );
};

export default Product;
