import { FcDeleteDatabase } from "react-icons/fc";
import { useDispatch } from "react-redux";
import {add, remove} from "../redux/Slices/CartSlice";
import {toast} from "react-hot-toast";

const CartItem = ({item, itemIndex}) => {

  const dispatch = useDispatch();
  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.error("Item Removed")
  }
  return (
    <div>
      <div className="flex justify-center items-center mx-auto mt-10">
        <div className="h-[180px]">
          <img src={item.image} className="h-full w-full"></img>
        </div>
        <div className="flex flex-col justify-center items-start mt-10">
          <p className="text-gray-700 font-semibold text-lg text-left  mt-1 w-full">{item.title}</p>
          <p>{item.description.split(" ").slice(0,10).join(" ") + "..."}</p>
          <div>
            <p>{item.price}</p>
            <div onClick={removeFromCart}>
              <FcDeleteDatabase></FcDeleteDatabase>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
