import { useDispatch, useSelector } from "react-redux";
import {add, remove} from "../redux/Slices/CartSlice";
import {toast} from "react-hot-toast";

const Product = ({item}) => {

  const {cart} = useSelector((state) => state);

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(item));
    toast.success("Item added to cart");
  }

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.error("Item removed");
  }

  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 transition duration-300 ease-in
    hover:shadow-[rgba(0,_0,_0,_0.4)_0px_30px_90px]">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">{item.title}</p>
      </div>
      <div>
        <p>{item.description.split(" ").slice(0,10).join(" ") + "..."}</p>
      </div>
      <div>
        <img src={item.image}></img>
      </div>
      <div>
        <p>{item.price}</p>
      </div>
      <button>
        {
          cart.some((p) => p.id === item.id) ? 
          (<button onClick={removeFromCart}>Remove item</button>) 
          : 
          (<button onClick={addToCart}>Add to cart</button>)
        }
      </button>
    </div>
  );
};

export default Product;
